import React from 'react';
import Navbar from '../component/MainView/Navbar';

const Homepage = () => {
  return (
    <>
      <Navbar />
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        backgroundColor: '#f4f4f4',
        textAlign: 'center',
        padding: '20px'
      }}>
        <h1 style={{ fontSize: '36px', fontWeight: 'bold', color: '#1d4ed8' }}>Welcome to Student Helper App</h1>
        <p style={{ fontSize: '18px', color: '#4b5563', maxWidth: '600px', margin: '20px 0' }}>
          Your one-stop solution for academic guidance, mentorship, and career support. 
          Connect with experts, enhance your skills, and achieve your professional goals.
        </p>
        <button style={{
          padding: '10px 20px',
          fontSize: '16px',
          fontWeight: 'bold',
          color: '#ffffff',
          backgroundColor: '#1d4ed8',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
          transition: 'background 0.3s'
        }}
        onMouseOver={(e) => e.target.style.backgroundColor = '#153e75'}
        onMouseOut={(e) => e.target.style.backgroundColor = '#1d4ed8'}>
          Get Started
        </button>
      </div>
    </>
  );
};

export default Homepage;
